mapper.update:
update goods_category
set is_deleted = 1
where id = #{id}

mapper.insert:
insert into goods_category (id, name, pid, pic_url,
  gmt_create, gmt_modified, is_deleted
  )
values (#{goodsCategory.id,jdbcType=BIGINT}, #{goodsCategory.name,jdbcType=VARCHAR},
  #{goodsCategory.pid,jdbcType=BIGINT}, #{goodsCategory.picUrl,jdbcType=VARCHAR},
  #{goodsCategory.gmtCreate,jdbcType=TIMESTAMP}, #{goodsCategory.gmtModified,jdbcType=TIMESTAMP},
  #{goodsCategory.beDeleted,jdbcType=BIT}
  )
